import { Component } from '@angular/core';
import { FormBuilder, FormGroup, FormArray } from '@angular/forms';
@Component({
  selector: 'app-matrix',
  templateUrl: './matrix.component.html',
  styleUrls: ['./matrix.component.scss']
})
export class MatrixComponent {
  matrixForm: FormGroup;

  constructor(private fb: FormBuilder) {
    this.matrixForm = this.fb.group({
      matrix: this.fb.array([this.initRow()])
    });
  }

  initRow() {
    return this.fb.array([0]); // Initialize with one cell
  }

  get matrix(): FormArray {
    return this.matrixForm.get('matrix') as FormArray;
  }

  addRow() {
    this.matrix.push(this.initRow());
  }

  addColumn() {
    this.matrix.controls.forEach( row => (row as FormArray).push(this.fb.control(0)));
  }

  onSubmit() {
    console.log(this.matrixForm.value);
    // Process your matrix data here
  }

}
