import { Component,OnInit } from '@angular/core';
import { Observable, Subscription } from 'rxjs';
import { FormBuilder, FormGroup, FormArray,  FormControl, AbstractControl, Validators } from '@angular/forms';
import { ApiService } from '../api.service';
@Component({
  selector: 'app-matrix',
  templateUrl: './matrix.component.html',
  styleUrls: ['./matrix.component.scss']
})

export class MatrixComponent implements OnInit {
  matrixForm!: FormGroup;
  degreeForm!: FormGroup;
  degree:number=0; 


  profileForm = this.formb.group({
    degree: [''],

  });





  constructor(private fb: FormBuilder,  private formb: FormBuilder, private apiService: ApiService) {

  }

  n:number = 0  
  ngOnInit(): void {
    this.degreeForm = this.fb.group({
      degree: [this.degree, Validators.required]
  });



  this.matrixForm = this.fb.group({
      matrix: this.fb.array([]),
      // Uncomment and initialize ordinates if needed
      // ordinates: this.fb.array([]),
  });

  }
  coefitients=[1,2,3,1,1,1,1,1,1,1]
  ordinates=  [4,5,1,1,1,1,1,1,1,1]



  addColumn(){}
  addRow(){}
  addOrdinate(){}

  get matrixControls() {
    return (this.matrixForm.get('matrix') as FormArray).controls;
}

  onSubmitEquationsDegree() {
    console.log('degreeform is now - ', this.degreeForm.controls['degree'].value, ' <-')
  }

  onSubmit(): void {

      const formData = this.matrixForm.getRawValue();
      const payload = {
        coefficients: this.coefitients,   /* formData.matrix, */
        ordinates:  this.ordinates     /*formData.ordinates*/
      };

      this.apiService.postMatrixData(payload).subscribe({


        next: response => {
          console.log('Response:', response)
        },
        error: error => console.error('Error Response:', error)
      });


    }

    onSubmit_degree() {
      console.log('degree is now - ', this.profileForm.controls['degree'].value)  
    }

  }
