import { Component,OnInit } from '@angular/core';
import { FormBuilder, FormGroup, FormArray,  FormControl,  Validators } from '@angular/forms';
import { ApiService } from '../api.service';
@Component({
  selector: 'app-matrix',
  templateUrl: './matrix.component.html',
  styleUrls: ['./matrix.component.scss']
})

export class MatrixComponent implements OnInit {
  matrixForm!: FormGroup;
  degreeForm!: FormGroup;
  degree:number=0; 
  numberOfRows: number = 3; 
  numberOfColumns: number = 4;



  myForm: FormGroup = this.fb.group({
    arrayOfArrays: this.fb.array([])
  });

  profileForm = this.formb.group({
    degree: [''],

  });





  coefitients=[1,2,3,1,1,1,1,1,1,1]
  ordinates=  [4,5,1,1,1,1,1,1,1,1]



  constructor(private fb: FormBuilder, private fbuilder:FormBuilder,  private formb: FormBuilder, private apiService: ApiService) {

  }

  n:number = 0  
  ngOnInit(): void {
    this.degreeForm = this.fb.group({
      degree: [this.degree, Validators.required]
    });

    this.profileForm = this.fb.group({
      degree: ['']
    });
    //new form array of arrays

    this.addArray();
    this.initializeForm();
  }

  //begin of the methods array of arrays
  get arrayOfArrays(): FormArray {
    return this.myForm.get('arrayOfArrays') as FormArray;
  }

  initializeForm(): void {
    this.myForm = this.fb.group({
      matrix: this.fb.array(this.createRows())
    });
  }

  createRows(): FormGroup[] {
    return Array.from({ length: this.numberOfRows }, () => {
      return this.fb.group({
        columns: this.fb.array(this.createColumns())
      });
    });
  }

  createColumns(): FormControl[] {
    return Array.from({ length: this.numberOfColumns }, () => this.fb.control(''));
  }


  getControls(formArrayName: string, formGroupNameIndex: number): FormControl[] {
    return (this.myForm.get([formArrayName, formGroupNameIndex]) as FormArray).controls as FormControl[];
  }

  addArray(): void {
    const array = this.fb.array([]);
    this.arrayOfArrays.push(array);
  }

  addElementToArray(arrayIndex: number): void {
    const array = this.arrayOfArrays.at(arrayIndex) as FormArray;
    array.push(this.fb.control(''));
  }

  removeElementFromArray(arrayIndex: number, elementIndex: number): void {
    const array = this.arrayOfArrays.at(arrayIndex) as FormArray;
    array.removeAt(elementIndex);
    }

  onSubmit2(): void {
    console.log(this.myForm.value);
    }
  //end of the methods array of arrays

  get coefficientsControls() {
    return (this.matrixForm.get('coefficients') as FormArray).controls as FormGroup[];
  }

  get ordinatesControls() {
    return (this.matrixForm.get('ordinates') as FormArray).controls;
  }




  get matrixControls() {
    return (this.matrixForm.get('matrix') as FormArray).controls;
}

  onSubmitEquationsDegree() {
    console.log('degreeform is now - ', this.degreeForm.controls['degree'].value, ' <-')
  }

    onSubmit1(): void {
      const formData = this.profileForm.value;
      this.apiService.postDegreeData(formData).subscribe({
        next:response => console.log('Response:', response),
        error: error => console.error('Error:', error)
      });
    }


  initForm() {
    for (let i = 0; i < this.degree; i++) {
      (this.matrixForm.get('coefficients') as FormArray).push(this.initCoefficientRow());
      (this.matrixForm.get('ordinates') as FormArray).push(this.fb.control(0));
    }
  }

    initCoefficientRow(): FormGroup {
      const row = this.fb.group({});
      for (let i = 0; i < this.degree; i++) {
        row.addControl(`coeff${i}`, this.fb.control(0));
      }
      return row;
    }

    onSubmit(): void {
      const formData = this.matrixForm.value;
      this.apiService.postMatrixData(formData).subscribe({
        next: response => console.log('Response:', response),
        error : error => console.error('Error:', error)
      });
    }

  }
