import { Component,OnInit } from '@angular/core';
import { FormBuilder, FormGroup, FormArray,  FormControl, AbstractControl  } from '@angular/forms';
import { ApiService } from '../api.service';
@Component({
  selector: 'app-matrix',
  templateUrl: './matrix.component.html',
  styleUrls: ['./matrix.component.scss']
})

export class MatrixComponent implements OnInit {
  matrixForm!: FormGroup;

  constructor(private fb: FormBuilder, private apiService: ApiService) {}

  ngOnInit() {
    this.matrixForm = this.fb.group({
      matrix: this.fb.array([this.initRow()])
    });
  }

  initRow(): FormArray {
    return this.fb.array([this.initColumn()]);
    console.log('init of rows');
  }

  initColumn(): FormControl {
    return this.fb.control(0);
  }

  get matrix(): FormArray {
    return this.matrixForm.get('matrix') as FormArray;
  }

  // Safe type casting to FormArray
  getControls(formArray: AbstractControl): AbstractControl[] {
    return (formArray as FormArray).controls;
  }

  addRow(): void {
    this.matrix.push(this.initRow());
  }

  addColumn(): void {
    this.matrix.controls.forEach(row => (row as FormArray).push(this.initColumn()));
  }

  onSubmit(): void {
    console.log('Matrix Values:', this.matrixForm.value);
    console.log('hey this is onsubmit');
    this.apiService.postMatrixData(this.matrixForm.value).subscribe(
      response => {
        console.log('Response from server:', response);
      },
      error => {
        console.error('Error:', error);
      }
    );
  }
  }
