import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ApiService } from '../api.service';

@Component({
  selector: 'app-equation-solver',
  templateUrl: './equation-solver.component.html',
  styleUrls: ['./equation-solver.component.css']
})
export class EquationSolverComponent {
  equationForm: FormGroup;
  n: number = 0;
  maxN: number = 6;
  coefficients: number[][];

  constructor(private fb: FormBuilder, private apiService: ApiService) {
    this.createForm();
  }

  createForm() {
    this.equationForm = this.fb.group({
      n: ['', [Validators.required, Validators.min(1), Validators.max(this.maxN)]],
      coefficients: this.fb.array([])
    });
  }

  onNChange() {
    const nValue = this.equationForm.get('n').value;
    this.n = nValue;
    this.buildCoefficientForm(nValue);
  }

  buildCoefficientForm(n: number) {
    // Logic to dynamically build the form based on the value of n
  }

  onSubmit() {
    // Submit logic
  }
}
