import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class ApiService {
  apiUrl = 'http://localhost:8000';  // URL of my Django API

  constructor(private http: HttpClient) { }

  getSolverData() {
    return this.http.post<any>(`${this.apiUrl}/calculator/solve/`);
  }
}
