import { Component,OnInit } from '@angular/core';
import { FormBuilder, FormGroup, FormArray,  FormControl, AbstractControl,  Validators } from '@angular/forms';
import { ApiService } from '../api.service';
@Component({
  selector: 'app-matrix',
  templateUrl: './matrix.component.html',
  styleUrls: ['./matrix.component.scss']
})

export class MatrixComponent implements OnInit {

  degreeForm!: FormGroup;
  degree:number=0; 

  matrixForm!: FormGroup;
  rows = Array(4);
  cols = Array(4);

 // ordinatesForm!: FormGroup;

  profileForm = this.formb.group({
    degree: [''],
  });

  matrixform: FormGroup = this.formb.group({
    row: this.formb.array([])
});

 ordinatesForm: FormGroup = this.formbOrdinates.group({
  row: this.formbOrdinates.array([])
 })







  constructor(private fb: FormBuilder, private fbuilder:FormBuilder,  private formb: FormBuilder, private formbOrdinates: FormBuilder,
     private apiService: ApiService) {

  }

  n:number = 0  
  ngOnInit(): void {
    this.degreeForm = this.fb.group({
      degree: [this.degree, Validators.required]
    });

    this.profileForm = this.fb.group({
      degree: ['']
    });

    this.matrixForm = this.formb.group({});
    for (let i = 0; i < 4; i++) {
      for (let j = 0; j < 4; j++) {
          const controlName = `cell${i}_${j}`;
          this.matrixForm.addControl(controlName, new FormControl('', [
              Validators.required,
              Validators.pattern(/^\d*\.?\d+$/) // Only numeric values alowed 
          ]));
      }
  }

    this.ordinatesForm = this.formb.group({});
    for (let i = 0; i < 4; i++) {
      {
        this.ordinatesForm.addControl('ordin' + i , new FormControl(''));
      }
    }

  }


    onSubmit1(): void {
      const formData = this.profileForm.value;
      this.apiService.postDegreeData(formData).subscribe({
        next:response => console.log('ResponseDegree:', response),
        error: error => console.error('Error:', error)
      });
    }

    submitMatrixForm():void {
      const matrixData = this.matrixForm.value;
      console.log(this.matrixForm.value);
      if (this.matrixForm.valid) {
        // Process the valid form data
        this.apiService.postMatrixData(matrixData).subscribe({
          next: response => console.log('ResponseMatrix:', response),
          error: error => {
            console.log('error in submiting matrix -', error);
            this.matrixForm.setErrors({ 'backend': error.error.error });
          }
      });
    } else {
        // Optionally handle the invalid form state
        console.log('Invalid matrix');
    }


    }
    submitOrdinatesForm():void {
      const ordinatesData = this.ordinatesForm.value;
      console.log(this.ordinatesForm.value);
      this.apiService.postOrdinatesData(ordinatesData).subscribe({
        next: response => console.log('ResponseOrdinates:', response),
        error: error => console.error('Error:', error)
      })

    }


}

<div class="forms-container">
    <div class="form-profile">
        <form  [formGroup]="profileForm" (ngSubmit)="onSubmit1()" class="profile" novalidate >
            <label name= "degree"></label>
            <input type="number" formControlName = "degree" name="degree" class ="form-control round" placeholder="degree here">
            <button class="btn btn-secondary">Enter</button>
        </form>
    </div>
    <div class="form-matrix">
        <form [formGroup]="matrixForm" (ngSubmit)="submitMatrixForm()" class="rounded">
            <div class="formWithButton">
                <div *ngFor="let row of rows; let i = index" class="matrix-row">
                    <div *ngFor="let col of cols; let j = index" class="matrix-cell">
                        <div class="cell-container">
                            <input type="number" [formControlName]="'cell' + i + '_' + j" class="form-control" id="cell">
                            <label for="cell" class="badge bg-secondary bgcell" > x{{j+1}}</label>
                            <div *ngIf="matrixForm.get('cell' + i + '_' + j)?.errors?.['pattern']" class="error-message">
                                Only numbers are allowed.
                            </div>
                        </div>
                    </div>
                </div>
                <div *ngIf="matrixForm.errors?.['backend']" class="alert alert-dark error-message" role="alert" >
                    {{ matrixForm.errors?.['backend'] }}
                </div>

                <button type="submit" class="btn btn-primary buttonwidth">Submit</button>
            </div>
        </form>
    </div> 
    <div class="form-ordinates">  
        <form [formGroup]="ordinatesForm" class="ordinates-row" (ngSubmit) = "submitOrdinatesForm()">
            <div *ngFor="let row of rows; let i = index" class="form-control rounded ordinates-cell">
                    <input type ="number" class="rounded input-ordinate" [formControlName]="'ordin'+ i"/>
            </div>
            <button type="submit" class="btn btn-primary buttonwidth">Submit</button>
        </form>
    </div>
</div>
